    public void position (JInternalFrame frame) {
        int x = (Desktop.getWidth() / 2) - (frame.getWidth()/2);
        int y = (Desktop.getHeight() / 2) - (frame.getHeight()/2);
        frame.setLocation(x, y);

    }
    
    boolean frameExists(JInternalFrame frame) {
        JInternalFrame[] areActives=Desktop.getAllFrames();
        boolean exists=false;  
        if(areActives.length == 0) {
             return exists;
        }
        
        if(areActives.length > 0) {    
            for (int i=0; i < areActives.length; i++) {
                if(areActives[i]!=frame){
                    exists=true;                 
                }
            }
            return exists;
        }
        
        return exists;
    }
    
    
    private void clienteBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_clienteBtnMouseClicked
        Clientes clientes = new Clientes(); 
            if (!frameExists(clientes)){
                Desktop.add(clientes);
                position(clientes);
                clientes.setVisible(true);
            } 
    }//GEN-LAST:event_clienteBtnMouseClicked

    
