    private void vendedorBtnMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_vendedorBtnMouseClicked
        if(esAdministrador){         
            Vendedor vendedor = new Vendedor();
            if (!frameExists(vendedor)){
                Desktop.add(vendedor);
                position(vendedor);
                vendedor.setVisible(true);
            } 
        } else {
            JOptionPane.showMessageDialog(null,
                "Acceso solo para administrador",
                "Atención",
                JOptionPane.ERROR_MESSAGE
            );
        }
    }//GEN-LAST:event_vendedorBtnMouseClicked

    private void salirMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_salirMouseClicked
        InicioSesion login = new InicioSesion();
        login.setVisible(true);
        this.dispose();
    }//GEN-LAST:event_salirMouseClicked

